        "avis_comm": null,
        "avis_gouv": null,
        "texte": "<p>Alinéa 11</p><p>Remplacer les mots :</p><p>l’architecture</p><p>par les mots :</p><p>la culture</p>",
        "expose": "<p>Rédactionnel.</p>",
        "signataires": "Mme D. Gillot, rapporteure",
        "source": "http://www.senat.fr/amendements/commissions/2012-2013/614/Amdt_COM-57.html",
        "sous_amendement_de": null,
        "cle_unicite": "a74b9a1471ebe9930c52e35e428a86dc",
